mapper.update:
UPDATE style
SET title=#{title},content=#{content}, pay=#{pay}, times=#{times}, image=#{image},sizes=#{sizes},thumb=#{thumb}
WHERE styleno=#{styleno}

mapper.select:
 SELECT styleno, categoryno, managerno, title, name, rname, like1, email, content,cnt,image,image_name,thumb,sizes,pay,times,rdate
 FROM style
WHERE styleno=#{styleno}

mapper.update:
UPDATE style
SET cnt = cnt + 1 
WHERE styleno=#{styleno}

UPDATE style
SET like1 = like1 + 1 
WHERE styleno=#{styleno}

mapper.select:
SELECT styleno,categoryno, managerno, title, name, rname, like1, email, content,cnt,image,image_name,thumb,sizes,pay,times,rdate, r FROM ( SELECT styleno,categoryno, managerno, title, name, rname, like1, email, content,cnt,image,image_name,thumb,sizes,pay,times,rdate, rownum as r FROM ( SELECT styleno,categoryno, managerno, title, name, rname, like1, email, content,cnt,image,image_name,thumb,sizes,pay,times,rdate FROM style
WHERE categoryno=#{categoryno}
WHERE categoryno=#{categoryno} AND title LIKE '%' || #{title} || '%'
ORDER BY rdate DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

SELECT COUNT(*) as cnt FROM style
WHERE categoryno=#{categoryno}
WHERE categoryno=#{categoryno} AND title LIKE '%' || #{title} || '%'
